settings.custom_liquid:
{% comment %}
  Wishlist Section - Save as sections/wishlist.liquid
{% endcomment %}

<div class="wishlist-container">
  <div class="wishlist-header">
    <h1>{{ section.settings.heading | default: 'My Wishlist' }}</h1>
  </div>

  <div class="wishlist-content">
    <div class="wishlist-empty" style="display: none;">
      <p>{{ section.settings.empty_message | default: 'Your wishlist is empty' }}</p>
      <a href="{{ routes.collections_url }}" class="btn btn-primary">Continue Shopping</a>
    </div>

    <div class="wishlist-items">
      <div class="wishlist-table">
        <div class="wishlist-table-header">
          <div class="wishlist-col-product">{{ section.settings.product_label | default: 'PRODUCT' }}</div>
          <div class="wishlist-col-price">{{ section.settings.price_label | default: 'PRICE' }}</div>
          <div class="wishlist-col-stock">{{ section.settings.stock_label | default: 'STOCK STATUS' }}</div>
          <div class="wishlist-col-actions"></div>
        </div>

        <div class="wishlist-table-body" id="wishlist-items">
          <!-- Wishlist items will be populated here via JavaScript -->
        </div>
      </div>
    </div>
  </div>

  <div class="wishlist-share">
    <span>{{ section.settings.share_label | default: 'Share:' }}</span>
    <div class="share-buttons">
